import React, { useState, useEffect } from 'react';
import { Phone, MessageCircle, Image } from 'lucide-react';

// Google G Logo SVG Component
const GoogleGLogo = () => (
  <svg viewBox="0 0 48 48" className="w-6 h-6">
    <path
      d="M 42.6 24 C 42.6 22.5 42.4 21.1 42.1 19.7 L 24 19.7 L 24 26.7 L 34.5 26.7 C 33.9 29.4 32.2 31.7 29.7 33.3 L 29.7 37.8 L 36.3 37.8 C 40.1 34.3 42.6 29.5 42.6 24 Z"
      fill="#4285F4"
    />
    <path
      d="M 24 43.5 C 29.8 43.5 34.7 41.6 38.3 37.8 L 31.7 33.3 C 29.7 34.7 27.1 35.5 24 35.5 C 18.2 35.5 13.3 31.6 11.5 26.4 L 4.7 26.4 L 4.7 31 C 8.3 38.4 15.6 43.5 24 43.5 Z"
      fill="#34A853"
    />
    <path
      d="M 11.5 26.4 C 11 25.1 10.7 23.7 10.7 22.3 C 10.7 20.9 11 19.5 11.5 18.2 L 11.5 13.6 L 4.7 13.6 C 3.1 16.8 2.2 20.4 2.2 22.3 C 2.2 24.2 3.1 27.8 4.7 31 L 11.5 26.4 Z"
      fill="#FBBC05"
    />
    <path
      d="M 24 9.1 C 27.4 9.1 30.5 10.2 32.9 12.5 L 38.7 6.7 C 34.7 2.9 29.8 1 24 1 C 15.6 1 8.3 6.1 4.7 13.6 L 11.5 18.2 C 13.3 13 18.2 9.1 24 9.1 Z"
      fill="#EA4335"
    />
  </svg>
);

const VoiceAssistant = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [response, setResponse] = useState('Hi, how can I help?');
  const [isLoading, setIsLoading] = useState(false);
  const [searchResults, setSearchResults] = useState('');

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = true;

      recognition.onresult = (event) => {
        const current = event.resultIndex;
        const result = event.results[current];
        const text = result[0].transcript;
        setTranscript(text);
        if (result.isFinal) {
          handleSearch(text);
        }
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      window.recognition = recognition;
    }
  }, []);

  const handleSearch = async (searchText) => {
    setIsLoading(true);
    // Simulate search delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    setResponse(`Search results for: ${searchText}`);
    setSearchResults(`Here are the results for "${searchText}"`);
    setIsLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (transcript.trim()) {
      handleSearch(transcript);
    }
  };

  const toggleListening = () => {
    if (!isListening) {
      window.recognition?.start();
      setIsListening(true);
      setResponse('');
      setSearchResults('');
    } else {
      window.recognition?.stop();
      setIsListening(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white p-6 relative">
      {/* App Icons */}
      <div className="flex justify-center space-x-6 mb-8">
        <div className="flex flex-col items-center">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <Phone className="w-6 h-6 text-blue-500" />
          </div>
        </div>
        <div className="flex flex-col items-center">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <MessageCircle className="w-6 h-6 text-blue-500" />
          </div>
        </div>
        <div className="flex flex-col items-center">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <div className="w-6 h-6 bg-red-500 rounded-full" />
          </div>
        </div>
        <div className="flex flex-col items-center">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <Image className="w-6 h-6 text-blue-500" />
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex flex-col items-center justify-center">
        {!isListening ? (
          <form onSubmit={handleSubmit} className="flex items-center justify-center mb-8 w-full">
            <div className="bg-gray-800 rounded-full px-6 py-3 flex items-center w-full max-w-xl">
              {/* <GoogleGLogo /> */}
              <input
                type="text"
                className="bg-transparent flex-1 focus:outline-none text-white ml-4"
                placeholder="Search..."
                value={transcript}
                onChange={(e) => setTranscript(e.target.value)}
              />
              <button type="button" onClick={toggleListening}>
                <div className="w-5 h-5 bg-gray-400 rounded-full" />
              </button>
            </div>
          </form>
        ) : (
          <div className="text-center mb-8 w-full max-w-xl">
            {transcript ? (
              <div className="text-2xl">{transcript}</div>
            ) : (
              <div className="text-gray-400">Listening...</div>
            )}
            <button onClick={toggleListening} className="mt-4">
              <div className="w-5 h-5 bg-blue-500 rounded-full animate-pulse mx-auto" />
            </button>
          </div>
        )}

        {/* Response Area */}
        <div className="text-center">
          {isLoading ? (
            <div className="flex justify-center space-x-2">
              <div className="w-2 h-2 bg-white rounded-full animate-bounce" style={{ animationDelay: '0s' }} />
              <div className="w-2 h-2 bg-white rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
              <div className="w-2 h-2 bg-white rounded-full animate-bounce" style={{ animationDelay: '0.4s' }} />
            </div>
          ) : searchResults ? (
            <div className="text-xl">{searchResults}</div>
          ) : null}
        </div>
      </div>

      {/* Google Colors Animation Bar with Enhanced Glow Effect */}
      <div 
        className={`absolute bottom-0 left-1/2 transform -translate-x-1/2 mb-4 transition-all duration-500 ease-in-out ${
          isListening ? 'w-11/12' : 'w-2/5'
        }`}
      >
        <div className="relative w-full h-1 overflow-visible">
          {/* Google color sections with glow effects */}
          <div 
            className="absolute left-0 w-1/4 h-full bg-blue-500 animate-pulse rounded-full"
            style={{
              boxShadow: isListening 
                ? '0 0 15px #4285f4, 0 0 30px #4285f4, 0 0 45px #4285f4, 0 0 60px #4285f4'
                : '0 0 10px #4285f4, 0 0 20px #4285f4, 0 0 30px #4285f4',
              animationDuration: isListening ? '1.2s' : '1.5s',
              animationDelay: '0s'
            }}
          />
          {/* Rest of the color sections... */}
          <div 
            className="absolute left-1/4 w-1/4 h-full bg-red-500 animate-pulse rounded-full"
            style={{
              boxShadow: isListening 
                ? '0 0 15px #ea4335, 0 0 30px #ea4335, 0 0 45px #ea4335, 0 0 60px #ea4335'
                : '0 0 10px #ea4335, 0 0 20px #ea4335, 0 0 30px #ea4335',
              animationDuration: isListening ? '1.2s' : '1.5s',
              animationDelay: '0.2s'
            }}
          />
          <div 
            className="absolute left-2/4 w-1/4 h-full bg-yellow-500 animate-pulse rounded-full"
            style={{
              boxShadow: isListening 
                ? '0 0 15px #fbbc05, 0 0 30px #fbbc05, 0 0 45px #fbbc05, 0 0 60px #fbbc05'
                : '0 0 10px #fbbc05, 0 0 20px #fbbc05, 0 0 30px #fbbc05',
              animationDuration: isListening ? '1.2s' : '1.5s',
              animationDelay: '0.4s'
            }}
          />
          <div 
            className="absolute left-3/4 w-1/4 h-full bg-green-500 animate-pulse rounded-full"
            style={{
              boxShadow: isListening 
                ? '0 0 15px #34a853, 0 0 30px #34a853, 0 0 45px #34a853, 0 0 60px #34a853'
                : '0 0 10px #34a853, 0 0 20px #34a853, 0 0 30px #34a853',
              animationDuration: isListening ? '1.2s' : '1.5s',
              animationDelay: '0.6s'
            }}
          />
        </div>
      </div>
    </div>
  );
};

