import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative', width: '300px', height: '300px' }}>
        {/* Blue shape that morphs */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '300px',
          height: '300px'
        }}>
          <svg width="300" height="300" viewBox="0 0 300 300">
            <path 
              className="morphing-shape"
              fill="#0066ff"
              d="M50,50 L250,50 L250,250 L50,250 Z"
            />
          </svg>
        </div>

        {/* Image container */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '200px',
            height: '200px',
            zIndex: 1
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          .morphing-shape {
            animation: morphShapes 6s linear forwards;
            transform-origin: center;
          }

          @keyframes morphShapes {
            /* Start as square */
            0% {
              d: path('M50,50 L250,50 L250,250 L50,250 Z');
              transform: rotate(0deg);
            }
            /* Hold square */
            15% {
              d: path('M50,50 L250,50 L250,250 L50,250 Z');
              transform: rotate(0deg);
            }
            /* Morph to hexagon */
            25% {
              d: path('M150,50 L223,100 L223,200 L150,250 L77,200 L77,100 Z');
              transform: rotate(30deg);
            }
            /* Hold hexagon */
            40% {
              d: path('M150,50 L223,100 L223,200 L150,250 L77,200 L77,100 Z');
              transform: rotate(30deg);
            }
            /* Morph to dodecagon */
            50% {
              d: path('M150,50 L200,73 L223,125 L223,175 L200,227 L150,250 L100,227 L77,175 L77,125 L100,73 Z');
              transform: rotate(60deg);
            }
            /* Hold dodecagon */
            65% {
              d: path('M150,50 L200,73 L223,125 L223,175 L200,227 L150,250 L100,227 L77,175 L77,125 L100,73 Z');
              transform: rotate(60deg);
            }
            /* Morph to circle */
            75% {
              d: path('M150,50 C208,50 250,92 250,150 C250,208 208,250 150,250 C92,250 50,208 50,150 C50,92 92,50 150,50 Z');
              transform: rotate(90deg);
            }
            /* Hold circle to end */
            100% {
              d: path('M150,50 C208,50 250,92 250,150 C250,208 208,250 150,250 C92,250 50,208 50,150 C50,92 92,50 150,50 Z');
              transform: rotate(90deg);
            }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
