import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstAnimation, setIsFirstAnimation] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstAnimation(false);
    }, 450); // Slightly less than animation duration to ensure smooth transition

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      <div 
        style={{
          width: '256px',
          height: '256px',
          position: 'relative'
        }}
      >
        <style>
          {`
            .image-container {
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
              opacity: 1;
              transition: opacity 0.05s ease;
            }
            .hidden {
              opacity: 0;
            }
            @keyframes firstAnimation {
              0% { 
                transform: rotate(0deg) scale(1); 
              }
              100% { 
                transform: rotate(180deg) scale(0.5);
              }
            }
            @keyframes secondAnimation {
              0% { 
                transform: rotate(0deg) scale(0.5); 
              }
              100% { 
                transform: rotate(180deg) scale(1);
              }
            }
          `}
        </style>
        <div 
          className={`image-container ${!isFirstAnimation ? 'hidden' : ''}`}
          style={{
            animation: 'firstAnimation 0.5s linear forwards'
          }}
        >
          <img
            src={firstImage}
            alt="First state image"
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
          />
        </div>
        <div 
          className={`image-container ${isFirstAnimation ? 'hidden' : ''}`}
          style={{
            animation: 'secondAnimation 0.5s linear forwards'
          }}
        >
          <img
            src={secondImage}
            alt="Second state image"
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default RotatingImage;
