import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  const getPolygonPoints = (centerX, centerY, radius) => {
    const points = [];
    for (let i = 0; i < 12; i++) {
      const angle = (i * 30) * Math.PI / 180;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      points.push(`${x},${y}`);
    }
    return points.join(' ');
  };

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative', width: '300px', height: '300px' }}>
        {/* Shape container */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '300px',
          height: '300px',
          animation: 'rotateShape 3s linear forwards'
        }}>
          <svg width="300" height="300" viewBox="0 0 300 300">
            <defs>
              <linearGradient id="shapeGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" className="gradient-start" />
                <stop offset="100%" className="gradient-end" />
              </linearGradient>
            </defs>
            <polygon 
              className="morphing-shape"
              fill="url(#shapeGradient)"
            />
          </svg>
        </div>

        {/* Image container centered */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '256px',
            height: '256px',
            zIndex: 1
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          .gradient-start {
            animation: gradientStart 3s linear forwards;
            stop-color: #0066ff;
          }

          .gradient-end {
            animation: gradientEnd 3s linear forwards;
            stop-color: #4d4dff;
          }

          @keyframes gradientStart {
            0% { stop-color: #0066ff; }
            50% { stop-color: #6600ff; }
            100% { stop-color: #9933ff; }
          }

          @keyframes gradientEnd {
            0% { stop-color: #4d4dff; }
            50% { stop-color: #8000ff; }
            100% { stop-color: #b366ff; }
          }

          .morphing-shape {
            animation: morphToPolygon 3s linear forwards;
          }

          @keyframes morphToPolygon {
            0% { 
              points: 0,0 300,0 300,300 0,300;
            }
            100% { 
              points: ${getPolygonPoints(150, 150, 150)};
            }
          }

          @keyframes rotateShape {
            from { transform: rotate(0deg); }
            to { transform: rotate(30deg); }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
