import React, { useEffect } from 'react';

const RotatingImage = () => {
  useEffect(() => {
    const button = document.querySelector('.rotating-button');

    const animateGradient = () => {
      button.classList.add('active');
    };

    const resetAnimation = () => {
      button.classList.remove('active');
    };

    button.addEventListener('mouseenter', animateGradient);
    button.addEventListener('mouseleave', resetAnimation);

    return () => {
      button.removeEventListener('mouseenter', animateGradient);
      button.removeEventListener('mouseleave', resetAnimation);
    };
  }, []);

  return (
    <button className="rotating-button">
      <span className="rotating-background"></span>
      <svg
        className="rotating-svg"
        width="120"
        height="120"
        viewBox="0 0 213 215"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <title>Rotating Gemini</title>
        <g clipPath="url(#clip0_49_57)">
          <path
            fillRule="evenodd"
            clipRule="evenodd"
            d="M322 10V0H321.999C321.984 3.37227 321.813 6.70787 321.494 10H322ZM-108 215H-107.999C-108 214.833 -108 214.667 -108 214.5C-108 211.299 -107.86 208.13 -107.586 205H-108V214.5V215ZM211 106.944C153.415 105.102 107.265 57.9707 107.001 0H106.999C106.735 57.9707 60.5852 105.102 3 106.944V107.056C60.7507 108.903 107 156.301 107 214.5C107 214.667 107 214.833 106.999 215H107.001C107 214.833 107 214.667 107 214.5C107 156.301 153.249 108.903 211 107.056V106.944Z"
            fill="#3D3E3E"
          />
        </g>
        <defs>
          <clipPath id="clip0_49_57">
            <rect width="213" height="215" fill="white" />
          </clipPath>
        </defs>
      </svg>
    </button>
  );
};

export default RotatingImage;

// CSS Styles
const styles = `
.rotating-button {
  position: relative;
  border: none;
  cursor: pointer;
  appearance: none;
  inline-size: 243px;
  block-size: 243px;
  background: transparent;
  overflow: hidden;
}

.rotating-background {
  position: absolute;
  inset-inline-start: 0;
  inset-block-start: 0;
  display: block;
  inline-size: 243px;
  block-size: 243px;
  background: radial-gradient(circle, #4480f4, #9f86e0, #4480f4);
  clip-path: path(
    "M0 121.5C0 54.3974 54.3974 0 121.5 0C188.603 0 243 54.3974 243 121.5C243 188.603 188.603 243 121.5 243C54.3974 243 0 188.603 0 121.5Z"
  );
  transform: scale(0);
  transition: transform 0.5s ease;
}

.rotating-button.active .rotating-background {
  transform: scale(1);
  animation: rotate 4s linear infinite;
}

.rotating-svg {
  position: absolute;
  inset-inline-start: 50%;
  inset-block-start: 50%;
  translate: -50% -50%;
  rotate: 0;
  transition: rotate 1.2s ease, filter 0.6s ease;
}

.rotating-button.active .rotating-svg {
  rotate: 360deg;
  animation: pulse 4s infinite;
}

@keyframes rotate {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

@keyframes pulse {
  0%, 100% {
    filter: brightness(1);
  }
  50% {
    filter: brightness(4);
  }
}`;
