import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);
  const [isSquare, setIsSquare] = useState(true);

  useEffect(() => {
    const imageTimer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    // Change to polygon after square rotation
    const shapeTimer = setTimeout(() => {
      setIsSquare(false);
    }, 3000);

    return () => {
      clearTimeout(imageTimer);
      clearTimeout(shapeTimer);
    };
  }, []);

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative' }}>
        {/* Shape container */}
        <div style={{
          width: '300px',
          height: '300px',
          position: 'relative'
        }}>
          {isSquare ? (
            // Square
            <div style={{
              width: '100%',
              height: '100%',
              backgroundColor: '#0066ff',
              animation: 'rotateSquare 3s linear forwards'
            }} />
          ) : (
            // 12-sided polygon
            <svg 
              width="300" 
              height="300" 
              viewBox="0 0 300 300"
              style={{
                animation: 'rotatePolygon 3s linear forwards'
              }}
            >
              <polygon 
                points="150,20 185,28 215,50 235,85 240,120 235,155 215,190 185,212 150,220 115,212 85,190 65,155 60,120 65,85 85,50 115,28"
                fill="#0066ff"
              />
            </svg>
          )}
        </div>

        {/* Image container */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '256px',
            height: '256px',
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes rotateSquare {
            from { transform: rotate(0deg); }
            to { transform: rotate(30deg); }
          }

          @keyframes rotatePolygon {
            from { transform: rotate(30deg); }
            to { transform: rotate(60deg); }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
