import React, { useState } from 'react';

const RotatingImage = () => {
  const [isActive, setIsActive] = useState(false);

  return (
    <div className="relative">
      <style>{`
        .rotating-button {
          position: relative;
          border: none;
          cursor: pointer;
          appearance: none;
          width: 60%; /* Scales dynamically */
          max-width: 300px; /* Optional: maximum size */
          aspect-ratio: 1; /* Keeps the button square */
          background: transparent;
          overflow: hidden;
        }

        .rotating-background {
          position: absolute;
          left: 0;
          top: 0;
          width: 100%; /* Dynamic width */
          height: 100%; /* Matches the aspect-ratio */
          background: #4480f4;
          clip-path: path("M0 50% C0 22% 22% 0 50% 0 C78% 0 100% 22% 100% 50% C100% 78% 78% 100% 50% 100% C22% 100% 0 78% 0 50%");
          transform: scale(0);
          transition: transform 0.5s ease;
        }

        .rotating-button.active .rotating-background {
          transform: scale(1);
          animation: morphPath 10s linear infinite,
                     rotate 7s linear infinite;
        }

        .rotating-svg {
          position: absolute;
          left: 50%;
          top: 50%;
          transform: translate(-50%, -50%);
          width: 70%; /* SVG scales dynamically */
          transition: transform 1.2s ease, filter 0.6s ease;
        }

        .rotating-button.active .rotating-svg {
          transform: translate(-50%, -50%) rotate(360deg);
          animation: pulse 4s infinite;
        }

        /* Responsive Media Queries */
        @media (max-width: 768px) {
          .rotating-button {
            width: 80%;
          }
        }

        @media (max-width: 480px) {
          .rotating-button {
            width: 90%;
          }
        }

        @keyframes morphPath {
          0% { clip-path: path("M0 50% C0 22% 22% 0 50% 0 C78% 0 100% 22% 100% 50% C100% 78% 78% 100% 50% 100% C22% 100% 0 78% 0 50%"); }
          25% { clip-path: path("M25% 10% C40% 0 60% 0 75% 10% C90% 20% 100% 40% 100% 60% C100% 80% 90% 100% 75% 90% C60% 80% 40% 80% 25% 90% C10% 80% 0 60% 0 40% C0 20% 10% 10% 25% 10%"); }
          50% { clip-path: path("M0 40% C0 20% 20% 0 40% 0 C60% 0 100% 20% 100% 40% C100% 60% 80% 100% 60% 100% C40% 100% 20% 80% 0 60% C0 40% 0 40% 0 40%"); }
          100% { clip-path: path("M0 50% C0 22% 22% 0 50% 0 C78% 0 100% 22% 100% 50% C100% 78% 78% 100% 50% 100% C22% 100% 0 78% 0 50%"); }
        }

        @keyframes rotate {
          0% { transform: rotate(0deg) scale(1); }
          100% { transform: rotate(360deg) scale(1); }
        }

        @keyframes pulse {
          0%, 100% { filter: brightness(1); }
          50% { filter: brightness(4); }
        }
      `}</style>

      <button 
        className={`rotating-button ${isActive ? 'active' : ''}`}
        onMouseEnter={() => setIsActive(true)}
        onMouseLeave={() => setIsActive(false)}
      >
        <span className="rotating-background"></span>
        <svg
          className="rotating-svg"
          viewBox="0 0 213 215"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          preserveAspectRatio="xMidYMid meet"
        >
          <g clipPath="url(#clip0_49_57)">
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M211 106.944C153.415 105.102 107.265 57.9707 107.001 0H106.999C106.735 57.9707 60.5852 105.102 3 106.944V107.056C60.7507 108.903 107 156.301 107 214.5C107 214.667 107 214.833 106.999 215H107.001C107 214.833 107 214.667 107 214.5C107 156.301 153.249 108.903 211 107.056V106.944Z"
              fill="#3D3E3E"
            />
          </g>
          <defs>
            <clipPath id="clip0_49_57">
              <rect width="213" height="215" fill="white" />
            </clipPath>
          </defs>
        </svg>
      </button>
    </div>
  );
};

export default RotatingImage;
