import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstAnimation, setIsFirstAnimation] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstAnimation(false);
    }, 1000); // Changed timing for image switch

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      {/* Blue square background with slow rotation */}
      <div style={{
        width: '300px',
        height: '300px',
        backgroundColor: '#0066ff',
        position: 'relative',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        animation: 'rotateBg 2s ease forwards'
      }}>
        {/* Image container with separate animation timing */}
        <div 
          style={{
            width: '256px',
            height: '256px',
            position: 'relative',
            transform: 'rotate(0deg)', // Reset any inherited rotation
            animation: isFirstAnimation 
              ? 'firstAnimation 1s ease-in forwards'
              : 'secondAnimation 1s ease-out forwards'
          }}
        >
          <style>
            {`
              @keyframes rotateBg {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(30deg); }
              }

              @keyframes firstAnimation {
                0% { transform: rotate(0deg) scale(1); }
                100% { transform: rotate(-90deg) scale(0.5); }
              }
              @keyframes secondAnimation {
                0% { transform: rotate(90deg) scale(0.5); }
                100% { transform: rotate(0deg) scale(1); }
              }
            `}
          </style>
          <img
            src={isFirstAnimation ? firstImage : secondImage}
            alt={isFirstAnimation ? "First state image" : "Second state image"}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default RotatingImage;
