import React, { useState, useEffect } from "react";

const MorphingAnimation = () => {
  const [currentShape, setCurrentShape] = useState(0);

  useEffect(() => {
    // Cycle through shapes every 4 seconds
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % 3);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  // Define SVG paths for the shapes
  const shapes = [
    "M150 10 C215 10 290 75 290 150 C290 225 215 290 150 290 C85 290 10 225 10 150 C10 75 85 10 150 10 Z", // Circle
    "M75 30 L225 30 L300 150 L225 270 L75 270 L0 150 Z", // Hexagon
    "M150 30 Q190 100 150 150 T150 270 Q110 200 150 150 T150 30", // Flower
  ];

  return (
    <div style={{ position: "relative", width: "300px", height: "300px" }}>
      {/* SVG container */}
      <svg
        width="300"
        height="300"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateShape 12s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#6a00ff" />
            <stop offset="100%" stopColor="#00d4ff" />
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 4s ease-in-out",
          }}
        />
      </svg>

      {/* Rotating Image */}
      <div
        className="rotating-image"
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          animation: "rotateImage 6s linear infinite",
        }}
      >
        <img
          src={currentShape === 0 ? "/s1.png" : "/s2.png"} // Example images
          alt="Morphing Center"
          style={{ width: "150px", height: "150px", borderRadius: "50%" }}
        />
      </div>

      {/* CSS Styling */}
      <style>
        {`
          @keyframes rotateShape {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .rotating-image {
            animation: rotateImage 6s linear infinite;
          }

          @keyframes rotateImage {
            from { transform: translate(-50%, -50%) rotate(0deg); }
            to { transform: translate(-50%, -50%) rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
};

export default MorphingAnimation;
