import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  // Define SVG paths for different shapes with larger dimensions
  const shapes = [
    // Large circle
    "M150 20 C230 20 280 70 280 150 C280 230 230 280 150 280 C70 280 20 230 20 150 C20 70 70 20 150 20 Z",
    // Large hexagon with smooth corners
    "M150 20 L250 70 L280 170 L220 260 L80 260 L20 170 L50 70 Z",
    // Large flower/wavy shape
    "M150 20 C200 20 280 70 280 150 C280 200 230 280 150 280 C70 280 20 200 20 150 C20 70 100 20 150 20 Z"
  ];

  useEffect(() => {
    // Cycle through shapes every 3 seconds
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      {/* Background morphing shape */}
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#00688B" className="gradient-start" /> {/* Sea blue */}
            <stop offset="100%" stopColor="#8A2BE2" className="gradient-end" /> {/* Purple */}
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      {/* Image container */}
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "200px",
          height: "200px",
          zIndex: 1
        }}
      >
        <img
          src={currentShape === 0 ? firstImage : secondImage}
          alt="Rotating star"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            animation: "rotateImage 4s linear infinite"
          }}
        />
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImage {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #00688B; }
            50% { stop-color: #4169E1; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #8A2BE2; }
            50% { stop-color: #483D8B; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
