import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative', width: '400px', height: '400px' }}>
        {/* Background shapes with gradients */}
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '400px',
          height: '400px'
        }}>
          <svg width="400" height="400" viewBox="0 0 400 400">
            <defs>
              <radialGradient id="seaPurpleGradient" cx="50%" cy="50%" r="50%">
                <stop offset="0%" className="gradient-start" stopColor="#00688B" /> {/* Sea blue */}
                <stop offset="100%" className="gradient-end" stopColor="#8A2BE2" /> {/* Purple */}
              </radialGradient>
            </defs>

            {/* Main shape that morphs */}
            <path 
              className="morphing-shape"
              fill="url(#seaPurpleGradient)"
            />
          </svg>
        </div>

        {/* Image container */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '260px',  // Original size
            height: '260px',
            zIndex: 1
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          .morphing-shape {
            animation: morphShapes 3s linear infinite;
            transform-origin: center;
          }

          @keyframes morphShapes {
            0%, 30% {
              /* Hexagon with rounded edges - 30% larger than image */
              d: path('M200,80 C230,80 260,95 280,125 C300,155 305,190 290,220 C275,250 250,275 220,285 C190,295 160,290 135,270 C110,250 95,220 95,190 C95,160 110,130 135,110 C160,90 185,80 200,80 Z');
            }
            33%, 63% {
              /* Ellipse - 30% larger */
              d: path('M200,100 C270,100 320,150 320,200 C320,250 270,300 200,300 C130,300 80,250 80,200 C80,150 130,100 200,100 Z');
            }
            66%, 100% {
              /* Flower shape - 30% larger */
              d: path('M200,80 C240,80 260,120 240,160 C260,200 240,240 200,240 C160,240 140,200 160,160 C140,120 160,80 200,80 Z');
            }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }

          .gradient-start {
            animation: colorShift1 3s linear infinite;
          }

          .gradient-end {
            animation: colorShift2 3s linear infinite;
          }

          @keyframes colorShift1 {
            0%, 30% { stop-color: #00688B; }
            50% { stop-color: #4B0082; }
            100% { stop-color: #00688B; }
          }

          @keyframes colorShift2 {
            0%, 30% { stop-color: #8A2BE2; }
            50% { stop-color: #00688B; }
            100% { stop-color: #8A2BE2; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
