import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative', width: '300px', height: '300px' }}>
        {/* Background shapes with gradients and star */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '300px',
          height: '300px'
        }}>
          <svg width="300" height="300" viewBox="0 0 300 300">
            {/* Gradients definitions */}
            <defs>
              <linearGradient id="purpleGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" className="gradient-start" stopColor="#e6e6fa"/>
                <stop offset="100%" className="gradient-end" stopColor="#4b0082"/>
              </linearGradient>
              <linearGradient id="blueGradient">
                <stop offset="0%" stopColor="#0066ff"/>
                <stop offset="100%" stopColor="#004db3"/>
              </linearGradient>
            </defs>

            {/* Main shape that morphs */}
            <path 
              className="morphing-shape"
              fill="url(#purpleGradient)"
            />

            {/* Four-pointed star */}
            <path
              className="star"
              fill="white"
              d="M150,120 L165,150 L195,165 L165,180 L150,210 L135,180 L105,165 L135,150 Z"
            />
          </svg>
        </div>

        {/* Image container */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '200px',
            height: '200px',
            zIndex: 1
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          .morphing-shape {
            animation: morphShapes 3s linear infinite;
            transform-origin: center;
          }

          .star {
            animation: rotateStar 3s linear infinite;
            transform-origin: center;
          }

          .gradient-start, .gradient-end {
            animation: colorShift 3s linear infinite;
          }

          @keyframes morphShapes {
            0%, 30% {
              /* Hexagon with rounded edges */
              d: path('M150,50 C170,50 190,60 205,80 C220,100 225,125 220,150 C215,175 200,195 180,205 C160,215 140,215 120,205 C100,195 85,175 80,150 C75,125 80,100 95,80 C110,60 130,50 150,50 Z');
            }
            33%, 63% {
              /* Ellipse */
              d: path('M150,70 C200,70 240,105 240,150 C240,195 200,230 150,230 C100,230 60,195 60,150 C60,105 100,70 150,70 Z');
            }
            66%, 100% {
              /* Flower shape */
              d: path('M150,50 C180,50 190,90 170,120 C190,150 180,190 150,190 C120,190 110,150 130,120 C110,90 120,50 150,50 Z');
            }
          }

          @keyframes rotateStar {
            0% { transform: rotate(0deg) scale(1); }
            50% { transform: rotate(180deg) scale(1.2); }
            100% { transform: rotate(360deg) scale(1); }
          }

          @keyframes colorShift {
            0%, 30% {
              stop-color: #e6e6fa;
            }
            33%, 63% {
              stop-color: #0066ff;
            }
            66%, 100% {
              stop-color: #9370db;
            }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
