import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  // Generate soft hexagon with star cutout
  const generateSoftHexagonWithStar = () => {
    // Main hexagon with curved edges
    const mainPath = [
      "M150 40",  // Start at top center
      "C180 40, 210 50, 235 75",  // Top right curve
      "C260 100, 270 130, 270 150", // Right curve
      "C270 170, 260 200, 235 225", // Bottom right curve
      "C210 250, 180 260, 150 260", // Bottom curve
      "C120 260, 90 250, 65 225",   // Bottom left curve
      "C40 200, 30 170, 30 150",    // Left curve
      "C30 130, 40 100, 65 75",     // Top left curve
      "C90 50, 120 40, 150 40",     // Back to top
      "Z"
    ].join(" ");

    // Star cutout shape
    const starPath = [
      "M150 100",  // Star points
      "L170 150",
      "L220 150",
      "L170 170",
      "L150 220",
      "L130 170",
      "L80 150",
      "L130 130",
      "Z"
    ].join(" ");

    return `${mainPath} ${starPath}`;
  };

  const shapes = [
    // Soft hexagon with star cutout (first shape)
    generateSoftHexagonWithStar(),
    // Slightly different hexagon (second shape)
    generateSoftHexagonWithStar(),
    // Third shape (can be modified for the flower if needed)
    generateSoftHexagonWithStar()
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [shapes.length]);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#B8A6FF" className="gradient-start" /> {/* Light purple */}
            <stop offset="100%" stopColor="#6F8FFF" className="gradient-end" /> {/* Light blue */}
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          fillRule="evenodd"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "200px",
          height: "200px",
          zIndex: 1
        }}
      >
        <img
          src={currentShape === 0 ? firstImage : secondImage}
          alt="Rotating star"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            animation: "rotateImage 4s linear infinite"
          }}
        />
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImage {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #B8A6FF; }
            50% { stop-color: #6F8FFF; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #6F8FFF; }
            50% { stop-color: #B8A6FF; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
