import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  // Cycle through shapes every 4 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentShape((prevShape) => (prevShape + 1) % 3);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  // Define shapes as SVG paths
  const shapes = [
    { id: 'hexagon', points: '75,30 225,30 300,150 225,270 75,270 0,150' }, // Hexagon
    { id: 'ellipse', cx: 150, cy: 150, rx: 150, ry: 100 }, // Ellipse
    { id: 'flower', path: 'M150 30 Q190 100 150 150 T150 270 Q110 200 150 150 T150 30' }, // Flower-like shape
  ];

  return (
    <div className="animation-container" style={{ position: 'relative', width: '300px', height: '300px' }}>
      {/* Shape container */}
      <svg width="300" height="300" viewBox="0 0 300 300" style={{ position: 'absolute', top: 0, left: 0 }}>
        <g className="morphing-shape">
          {currentShape === 0 && (
            <polygon points={shapes[0].points} fill="url(#gradientHex)" />
          )}
          {currentShape === 1 && (
            <ellipse cx={shapes[1].cx} cy={shapes[1].cy} rx={shapes[1].rx} ry={shapes[1].ry} fill="url(#gradientEllipse)" />
          )}
          {currentShape === 2 && (
            <path d={shapes[2].path} fill="url(#gradientFlower)" />
          )}
        </g>
        <defs>
          {/* Gradient definitions */}
          <linearGradient id="gradientHex" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#6a00ff" />
            <stop offset="100%" stopColor="#00d4ff" />
          </linearGradient>
          <linearGradient id="gradientEllipse" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#0066ff" />
            <stop offset="100%" stopColor="#33ccff" />
          </linearGradient>
          <linearGradient id="gradientFlower" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#ff66cc" />
            <stop offset="100%" stopColor="#6a00ff" />
          </linearGradient>
        </defs>
      </svg>

      {/* Rotating image */}
      <div
        className="rotating-image"
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%) rotate(360deg)',
          animation: 'rotateImage 6s linear infinite',
        }}
      >
        <img
          src={currentShape % 2 === 0 ? firstImage : secondImage}
          alt="Rotating Image"
          style={{ width: '150px', height: '150px', borderRadius: '50%' }}
        />
      </div>

      <style>
        {`
          @keyframes rotateImage {
            from { transform: translate(-50%, -50%) rotate(0deg); }
            to { transform: translate(-50%, -50%) rotate(360deg); }
          }

          .morphing-shape polygon,
          .morphing-shape ellipse,
          .morphing-shape path {
            animation: morphShape 4s ease-in-out infinite;
          }

          @keyframes morphShape {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
