import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstAnimation, setIsFirstAnimation] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstAnimation(false);
    }, 450);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rotating-container">
      <div 
        style={{
          width: '50px',
          height: '50px',
          position: 'relative',
          animation: isFirstAnimation 
            ? 'firstAnimation 0.25s linear forwards'
            : 'secondAnimation 0.25s linear forwards'
        }}
      >
        <style>
          {`
            @keyframes firstAnimation {
              0% { transform: rotate(0deg) scale(1); }
              100% { transform: rotate(180deg) scale(0.5); }
            }
            @keyframes secondAnimation {
              0% { transform: rotate(0deg) scale(0.5); }
              100% { transform: rotate(180deg) scale(1); }
            }
          `}
        </style>
        <img
          src={isFirstAnimation ? firstImage : secondImage}
          alt={isFirstAnimation ? "First state image" : "Second state image"}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover'
          }}
        />
      </div>
    </div>
  );
};

export default RotatingImage;
