import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';
import TwelvePetalFlower from './TwelvePetalFlower';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  // Define SVG paths with improved hexagon
  const shapes = [
    // Large circle
    "M150 20 C230 20 280 70 280 150 C280 230 230 280 150 280 C70 280 20 230 20 150 C20 70 70 20 150 20 Z",

    // Improved hexagon with equal sides and smooth corners
    "M150 20 C165 20 175 25 185 35 L245 95 C255 105 260 115 260 130 L260 170 C260 185 255 195 245 205 L185 265 C175 275 165 280 150 280 C135 280 125 275 115 265 L55 205 C45 195 40 185 40 170 L40 130 C40 115 45 105 55 95 L115 35 C125 25 135 20 150 20 Z",

    // Large flower/wavy shape
    "M150 20 C200 20 280 70 280 150 C280 200 230 280 150 280 C70 280 20 200 20 150 C20 70 100 20 150 20 Z",

    // Elongated fusing circles (new shape)
    "M100 150 C100 70 150 20 200 20 C250 20 300 70 300 150 C300 230 250 280 200 280 C150 280 100 230 100 150 Z M200 20 C250 20 300 70 300 150 C300 230 250 280 200 280 C150 280 100 230 100 150 C100 70 150 20 200 20 Z",

   // 12-Petal Flower
   Array.from({ length: 12 })
   .map((_, i) => {
     const angle = (i * 30) * (Math.PI / 180); // 30-degree intervals
     const x1 = 150 + 80 * Math.cos(angle); // Start of petal
     const y1 = 150 + 80 * Math.sin(angle);
     const x2 = 150 + 40 * Math.cos(angle - Math.PI / 12); // Control point 1
     const y2 = 150 + 40 * Math.sin(angle - Math.PI / 12);
     const x3 = 150 + 40 * Math.cos(angle + Math.PI / 12); // Control point 2
     const y3 = 150 + 40 * Math.sin(angle + Math.PI / 12);
     return `M150,150 L${x2},${y2} Q${x1},${y1} ${x3},${y3} Z`;
   })
   .join(" "),
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [shapes.length]);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#00688B" className="gradient-start" />
            <stop offset="100%" stopColor="#8A2BE2" className="gradient-end" />
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "200px",
          height: "200px",
          zIndex: 1
        }}
      >
        <img
          src={currentShape === 0 ? firstImage : secondImage}
          alt="Rotating star"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            animation: "rotateImage 4s linear infinite"
          }}
        />
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImage {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #00688B; }
            50% { stop-color: #4169E1; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #8A2BE2; }
            50% { stop-color: #483D8B; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
