import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  // Calculate regular hexagon points with equal sides
  const generateHexagonPath = (centerX, centerY, radius) => {
    const points = [];
    const angleStep = (Math.PI * 2) / 6;
    const cornerRadius = 20;  // Radius for rounded corners

    // Calculate main vertices of hexagon
    for (let i = 0; i < 6; i++) {
      const angle = i * angleStep - Math.PI / 2; // Start from top
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      points.push({ x, y });
    }

    // Create SVG path with rounded corners
    return points.reduce((path, point, index) => {
      const nextPoint = points[(index + 1) % 6];
      const prevPoint = points[(index - 1 + 6) % 6];

      // Calculate control points for smooth corners
      const dx = nextPoint.x - point.x;
      const dy = nextPoint.y - point.y;
      const angle = Math.atan2(dy, dx);

      if (index === 0) {
        return `M${point.x + cornerRadius * Math.cos(angle)} ${point.y + cornerRadius * Math.sin(angle)}`;
      }

      // Add cubic bezier curve for rounded corner
      return `${path} L${point.x - cornerRadius * Math.cos(angle)} ${point.y - cornerRadius * Math.sin(angle)} 
      Q${point.x} ${point.y} ${point.x + cornerRadius * Math.cos(angle)} ${point.y + cornerRadius * Math.sin(angle)}`;
    }, '') + 'Z';  // Close the path
  };

  // Define SVG paths with mathematically correct hexagon
  const shapes = [
    // Large circle
    "M150 20 C230 20 280 70 280 150 C280 230 230 280 150 280 C70 280 20 230 20 150 C20 70 70 20 150 20 Z",
    // Regular hexagon with equal sides and smooth corners
    generateHexagonPath(150, 150, 120),
    // Large flower/wavy shape
    "M150 20 C200 20 280 70 280 150 C280 200 230 280 150 280 C70 280 20 200 20 150 C20 70 100 20 150 20 Z"
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [shapes.length]);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#00688B" className="gradient-start" />
            <stop offset="100%" stopColor="#8A2BE2" className="gradient-end" />
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "200px",
          height: "200px",
          zIndex: 1
        }}
      >
        <img
          src={currentShape === 0 ? firstImage : secondImage}
          alt="Rotating star"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            animation: "rotateImage 4s linear infinite"
          }}
        />
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImage {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #00688B; }
            50% { stop-color: #4169E1; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #8A2BE2; }
            50% { stop-color: #483D8B; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
