import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);
  const [showPolygon, setShowPolygon] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    // Show polygon after square rotation
    const polygonTimer = setTimeout(() => {
      setShowPolygon(true);
    }, 3000);

    return () => {
      clearTimeout(timer);
      clearTimeout(polygonTimer);
    };
  }, []);

  return (
    <div className="rotating-container">
      {/* Outer container for positioning */}
      <div style={{ position: 'relative' }}>
        {/* Blue square with continuous rotation */}
        <div style={{
          width: '300px',
          height: '300px',
          backgroundColor: '#0066ff',
          animation: 'rotateBackground 3s linear forwards'
        }} />

        {/* 12-sided polygon */}
        {showPolygon && (
          <div style={{
            position: 'absolute',
            top: '0',
            left: '0',
            width: '300px',
            height: '300px',
            animation: 'rotatePolygon 2s linear forwards'
          }}>
            <svg width="300" height="300" viewBox="0 0 300 300">
              <polygon 
                points="150,25 202,39 238,85 238,134 202,180 150,194 98,180 62,134 62,85 98,39"
                fill="#0066ff"
                style={{
                  transformOrigin: 'center',
                  transform: 'scale(1.5)'
                }}
              />
            </svg>
          </div>
        )}

        {/* Image container positioned absolutely on top */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '256px',
            height: '256px',
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(30deg); }
          }

          @keyframes rotatePolygon {
            from { transform: rotate(0deg); }
            to { transform: rotate(30deg); }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
