import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';
import TwelvePetalFlower from './TwelvePetalFlower';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  const shapes = [

      // React logo-style hexagon
  Array.from({ length: 6 })
  .map((_, i) => {
    const angle = (i * 60) * (Math.PI / 180); // Angle for the current vertex
    const nextAngle = ((i + 1) * 60) * (Math.PI / 180); // Angle for the next vertex
    const midAngle = (angle + nextAngle) / 2; // Middle angle for control point

    const x1 = 150 + 130 * Math.cos(angle); // Current vertex
    const y1 = 150 + 130 * Math.sin(angle);
    const x2 = 150 + 100 * Math.cos(midAngle); // Control point inward
    const y2 = 150 + 100 * Math.sin(midAngle);
    const x3 = 150 + 130 * Math.cos(nextAngle); // Next vertex
    const y3 = 150 + 130 * Math.sin(nextAngle);

    return `${i === 0 ? "M${x1},${y1}" : ""} Q${x2},${y2} ${x3},${y3}`;
  })
  .join(" ") + " Z",
    // Large circle
    "M150 20 C230 20 280 70 280 150 C280 230 230 280 150 280 C70 280 20 230 20 150 C20 70 70 20 150 20 Z",




    // Elongated circle with star cutout
    "M150 40 C200 40 240 90 240 150 C240 210 200 260 150 260 C100 260 60 210 60 150 C60 90 100 40 150 40 Z",

   // Larger 8-Petal Flower
   Array.from({ length: 8 })
   .map((_, i) => {
     const angle = (i * 45) * (Math.PI / 180); // 30-degree intervals
     const x1 = 150 + 150 * Math.cos(angle); // Start of petal (larger radius)
     const y1 = 150 + 150 * Math.sin(angle);
     const x2 = 150 + 125 * Math.cos(angle - Math.PI / 8); // Control point 1 (slightly adjusted radius)
     const y2 = 150 + 125 * Math.sin(angle - Math.PI / 8);
     const x3 = 150 + 125 * Math.cos(angle + Math.PI / 8); // Control point 2
     const y3 = 150 + 125 * Math.sin(angle + Math.PI / 8);
     return `M150,150 L${x2},${y2} Q${x1},${y1} ${x3},${y3} Z`;
   })
   .join(" "),


  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [shapes.length]);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#00688B" className="gradient-start" />
            <stop offset="100%" stopColor="#8A2BE2" className="gradient-end" />
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "200px",
          height: "200px",
          zIndex: 1
        }}
      >
        <img
          src={currentShape === 0 ? firstImage : secondImage}
          alt="Rotating star"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            animation: "rotateImage 4s linear infinite"
          }}
        />
      </div>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImage {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #00688B; }
            50% { stop-color: #4169E1; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #8A2BE2; }
            50% { stop-color: #483D8B; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
