import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstImage, setIsFirstImage] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstImage(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  // Calculate points for regular polygons
  const getPolygonPoints = (centerX, centerY, radius, sides) => {
    const points = [];
    for (let i = 0; i < sides; i++) {
      const angle = (i * (360 / sides)) * Math.PI / 180;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      points.push(`${x},${y}`);
    }
    return points.join(' ');
  };

  // Get points for different shapes
  const squarePoints = "0,0 300,0 300,300 0,300";
  const hexagonPoints = getPolygonPoints(150, 150, 150, 6);
  const dodecagonPoints = getPolygonPoints(150, 150, 150, 12);
  // For circle, we'll use a different SVG element

  return (
    <div className="rotating-container">
      <div style={{ position: 'relative', width: '300px', height: '300px' }}>
        {/* Shape container */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '300px',
          height: '300px',
          animation: 'rotateShape 12s linear infinite'
        }}>
          <svg width="300" height="300" viewBox="0 0 300 300">
            <g className="morphing-shape">
              <polygon 
                fill="#0066ff"
                className="polygon-morph"
              />
              <circle 
                cx="150" 
                cy="150" 
                r="150" 
                fill="#0066ff" 
                className="circle-shape"
              />
            </g>
          </svg>
        </div>

        {/* Image container */}
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '256px',
            height: '256px',
            zIndex: 1
          }}
        >
          <div style={{
            width: '100%',
            height: '100%',
            animation: isFirstImage 
              ? 'rotateImageFirst 1s linear forwards'
              : 'rotateImageSecond 1s linear forwards'
          }}>
            <img
              src={isFirstImage ? firstImage : secondImage}
              alt={isFirstImage ? "First state" : "Second state"}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </div>
        </div>
      </div>

      <style>
        {`
          .polygon-morph {
            animation: morphSequence 12s linear infinite;
          }

          .circle-shape {
            opacity: 0;
            animation: fadeCircle 12s linear infinite;
          }

          @keyframes morphSequence {
            0% { 
              points: ${squarePoints};
              opacity: 1;
            }
            20% { 
              points: ${squarePoints};
              opacity: 1;
            }
            25% { 
              points: ${hexagonPoints};
              opacity: 1;
            }
            45% { 
              points: ${hexagonPoints};
              opacity: 1;
            }
            50% { 
              points: ${dodecagonPoints};
              opacity: 1;
            }
            70% {
              points: ${dodecagonPoints};
              opacity: 1;
            }
            75% {
              opacity: 0;
            }
            95% {
              opacity: 0;
            }
            100% {
              points: ${squarePoints};
              opacity: 1;
            }
          }

          @keyframes fadeCircle {
            0%, 70% {
              opacity: 0;
            }
            75% {
              opacity: 1;
            }
            95% {
              opacity: 1;
            }
            100% {
              opacity: 0;
            }
          }

          @keyframes rotateShape {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          @keyframes rotateImageFirst {
            0% { transform: rotate(0deg) scale(1); }
            100% { transform: rotate(180deg) scale(0.5); }
          }

          @keyframes rotateImageSecond {
            0% { transform: rotate(0deg) scale(0.5); }
            100% { transform: rotate(180deg) scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
