import React, { useState, useEffect } from 'react';

const RotatingImage = () => {
  const [currentShape, setCurrentShape] = useState(0);

  const shapes = [
    // Large circle
    "M150 20 C230 20 280 70 280 150 C280 230 230 280 150 280 C70 280 20 230 20 150 C20 70 70 20 150 20 Z",

    // Regular hexagon with equal sides and smooth corners
    (() => {
      const center = { x: 150, y: 150 };
      const radius = 130;
      const cornerRadius = 20;

      const vertices = Array.from({ length: 6 }, (_, i) => {
        const angle = (i * 60) * Math.PI / 180;
        return {
          x: center.x + radius * Math.cos(angle),
          y: center.y + radius * Math.sin(angle)
        };
      });

      const path = vertices.reduce((path, point, i) => {
        const next = vertices[(i + 1) % 6];
        const dx = next.x - point.x;
        const dy = next.y - point.y;
        const angle = Math.atan2(dy, dx);

        if (i === 0) {
          return `M${point.x + cornerRadius * Math.cos(angle)} ${point.y + cornerRadius * Math.sin(angle)}`;
        }

        return `${path} L${point.x - cornerRadius * Math.cos(angle)} ${point.y - cornerRadius * Math.sin(angle)} 
        Q${point.x} ${point.y} ${point.x + cornerRadius * Math.cos(angle)} ${point.y + cornerRadius * Math.sin(angle)}`;
      }, '');

      return path + 'Z';
    })(),

    // Large flower/wavy shape
    "M150 20 C200 20 280 70 280 150 C280 200 230 280 150 280 C70 280 20 200 20 150 C20 70 100 20 150 20 Z",

    // Fusing circles
    `M100 150 
    C100 90 150 40 200 40 
    C250 40 300 90 300 150 
    C300 210 250 260 200 260 
    C150 260 100 210 100 150 
    Z 
    M200 40 
    C250 40 300 90 300 150 
    C300 210 250 260 200 260 
    C150 260 100 210 100 150 
    C100 90 150 40 200 40 
    Z`,

   // Larger 12-Petal Flower
   Array.from({ length: 12 })
   .map((_, i) => {
     const angle = (i * 30) * (Math.PI / 180);
     const x1 = 150 + 150 * Math.cos(angle);
     const y1 = 150 + 150 * Math.sin(angle);
     const x2 = 150 + 125 * Math.cos(angle - Math.PI / 12);
     const y2 = 150 + 125 * Math.sin(angle - Math.PI / 12);
     const x3 = 150 + 125 * Math.cos(angle + Math.PI / 12);
     const y3 = 150 + 125 * Math.sin(angle + Math.PI / 12);
     return `M150,150 L${x2},${y2} Q${x1},${y1} ${x3},${y3} Z`;
   })
   .join(" ")
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentShape((prev) => (prev + 1) % shapes.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [shapes.length]);

  return (
    <div style={{ position: "relative", width: "400px", height: "400px" }}>
      <svg
        width="400"
        height="400"
        viewBox="0 0 300 300"
        xmlns="http://www.w3.org/2000/svg"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          animation: "rotateBackground 8s linear infinite",
        }}
      >
        <defs>
          <linearGradient id="morphGradient" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#00688B" className="gradient-start" />
            <stop offset="100%" stopColor="#8A2BE2" className="gradient-end" />
          </linearGradient>
        </defs>
        <path
          d={shapes[currentShape]}
          fill="url(#morphGradient)"
          style={{
            transition: "d 1.5s ease-in-out",
          }}
        />
      </svg>

      <style>
        {`
          @keyframes rotateBackground {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
          }

          .gradient-start {
            animation: shiftColorStart 6s linear infinite;
          }

          .gradient-end {
            animation: shiftColorEnd 6s linear infinite;
          }

          @keyframes shiftColorStart {
            0%, 100% { stop-color: #00688B; }
            50% { stop-color: #4169E1; }
          }

          @keyframes shiftColorEnd {
            0%, 100% { stop-color: #8A2BE2; }
            50% { stop-color: #483D8B; }
          }
        `}
      </style>
    </div>
  );
};

export default RotatingImage;
