import React, { useState, useEffect } from 'react';
import firstImage from './s1.png';
import secondImage from './s2.png';

const RotatingImage = () => {
  const [isFirstAnimation, setIsFirstAnimation] = useState(true);
  const [isSecondPhase, setIsSecondPhase] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsFirstAnimation(false);
      setIsSecondPhase(true);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="flex items-center justify-center w-full h-96">
      <div className="bg-gradient-to-r from-blue-500 to-purple-500 p-8 rounded-lg">
        <div 
          style={{
            animation: isSecondPhase 
              ? 'rotateAndEnlarge 3s linear forwards'
              : 'rotateAndShrink 3s linear forwards'
          }}
        >
          <style>
            {`
              @keyframes rotateAndShrink {
                0% { 
                  transform: rotate(0deg) scale(1); 
                }
                100% { 
                  transform: rotate(720deg) scale(0.5);
                }
              }
              @keyframes rotateAndEnlarge {
                0% { 
                  transform: rotate(0deg) scale(0.5); 
                }
                100% { 
                  transform: rotate(720deg) scale(1);
                }
              }
            `}
          </style>
          <img
            src={isFirstAnimation ? firstImage : secondImage}
            alt={isFirstAnimation ? "First state image" : "Second state image"}
            className="w-64 h-64 object-cover"
          />
        </div>
      </div>
    </div>
  );
};

export default RotatingImage;
